mapper.select:
SELECT id, user_id, name, description, created_at, updated_at
FROM knowledge_base
WHERE id = #{id}

SELECT id, user_id, name, description, created_at, updated_at
FROM knowledge_base
WHERE id = #{id} AND user_id = #{userId}

mapper.update:
UPDATE knowledge_base
SET name = #{name},
    description = #{description},
    updated_at = #{updatedAt}
WHERE id = #{id} AND user_id = #{userId}
