mapper.select:
SELECT id, username, password_hash, role, created_at, updated_at, school, phone, email, bio, avatar_url, gender, birthday
FROM users
WHERE username = #{username}

SELECT id, username, password_hash, role, created_at, updated_at, school, phone, email, bio, avatar_url, gender, birthday
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
SET updated_at = NOW()
WHERE id = #{id}

UPDATE users
school = #{school}, phone = #{phone}, email = #{email}, bio = #{bio}, avatar_url = #{avatarUrl}, gender = #{gender}, birthday = #{birthday}, updated_at = NOW()
WHERE id = #{id}
